import React, { Component } from 'react';
import Youtube from 'react-youtube';

/** Local CSS */
import './ExperienceBlock.component.css';


class ExpereinceBlock extends Component {
    // Each block will have a prop of image source
    // company description
    // original problem
    // solution developed string
    // solution developed gif or youtube
    // any challenges? 

    constructor(props) {
        super(props);
    }

    render() {
        const challengesList = this.props.experienceDetail.challenges.split(". ")
            .map(function (challenge, index) {
                return <li key={index}>{challenge}</li>;
            });

        const youtubeOption = {
            width: '768',
            height: '432',
            playerVars: { // https://developers.google.com/youtube/player_parameters
                autoplay: 1,
                loop: true
            }
        }

        return <div className="experience-block-container">
            {/* <img src={this.props.imgSrc} className="experience-block-image"></img> */}
            <div>{this.props.experienceDetail.companyName}</div>
            <div>{this.props.experienceDetail.companyDescription}</div>
            <div>
                <h3>Challenges / Problem</h3>
                <ul>
                    {challengesList}
                </ul>
            </div>
            <div>
                <h3>Solution Demo</h3>
                {
                    <Youtube videoId={this.props.experienceDetail.solutionYoutubeId} opts={youtubeOption} />
                }
            </div>
        </div>
    }

}

export default ExpereinceBlock;
