import React, { Component } from 'react';

/** Local CSS */
import './ExperienceBlock.component.css';


class ExpereinceBlock extends Component {
    // Each block will have a prop of image source
    // company description
    // original problem
    // solution developed string
    // solution developed gif or youtube
    // any challenges? 

    constructor(props){
        super(props);
    }

    render() {
        return <div className="experience-block-container">
            {/* <img src={this.props.imgSrc} className="experience-block-image"></img> */}
            <div>{this.props.experienceDetail.companyName}</div>
            <div>{this.props.experienceDetail.companyDescription}</div>
            <div>
                <h3>Challenges / Problem</h3>
                <ul>
                    {this.getLiElementFromChallengesProps()}
                </ul>
            </div>
        </div>
    }

    getLiElementFromChallengesProps = () => {
        const challengesList = this.props.challenges.split(". ");
        for (let i = 0; i < challengesList.length; i++){
            return <li>{challengesList[i]}</li>
        }
    }
}

export default ExpereinceBlock;
